	<script>
	$(document).ready(function(){
	$(this).scroll(function(){
		var $winheight = $(window).height();// get height of window
		var $winPos = $(document).scrollTop() + $winheight - 50;// get postion of scroll bar then add the window height and subtrack 50px, creating position 50 px above the bottom of the screen.
			$('img').each(function(){ // do this for each image
				var $pos = $(this).offset().top; //get the position for image
				if ($winPos > $pos){ // if the image is above the winPos do this
					$(this).addClass('animate');
				}
				else {// remove else if you want animation to happen once. 
					$(this).removeClass('animate');
				}
				// $('.pos').text($pos +' // '+ $winPos); // for testing 
			});
		});
});
	</script>

<script>
$(window).scroll(reOrder)
$(window).resize(reOrder)

function reOrder() {
  var mq = window.matchMedia("(min-width: 100px)");
  if (mq.matches) {
    $('.right-child').addClass('customm');
    $('.right-child h2').text('Scroll')
    var scroll = $(window).scrollTop(),
      topContent = $('.one').position().top - 25,
      sectionHeight = $('.left').height(),
      rightHeight = $('.right-child').height(),
      bottomContent = topContent + sectionHeight - rightHeight - 45;

    if (scroll > topContent && scroll < bottomContent) {
      $('.customm').removeClass('posAbs').addClass('posFix');
    } else if (scroll > bottomContent) {
      $('.customm').removeClass('posFix').addClass('posAbs');
    } else if (scroll < topContent) {
      $('.customm').removeClass('posFix');
    }
  } else {
    $('.right-child').removeClass('customm posAbs posFix');
    $('.right-child h2').text("fixed")
  }
}
</script>
	
<script >
// number count for stats, using jQuery animate
$('.counting').each(function() {
  var $this = $(this),
      countTo = $this.attr('data-count');
  
  $({ countNum: $this.text()}).animate({
    countNum: countTo
  },
  {
    duration: 3000,
    easing:'linear',
    step: function() {
      $this.text(Math.floor(this.countNum));
    },
    complete: function() {
      $this.text(this.countNum);
      //alert('finished');
    }
  });   

});
</script>

	
