<script>
	$(document).ready(function(){
	$(this).scroll(function(){
		var $winheight = $(window).height();
		var $winPos = $(document).scrollTop() + $winheight - 50; 
			$('img').each(function(){ 
				var $pos = $(this).offset().top; 
				if ($winPos > $pos){ 
					$(this).addClass('animate');
				}
				else { 
					$(this).removeClass('animate');
				}
			
			});
		});
});
	</script>

